mapper.select:
SELECT b.*, IFNULL( u.nickname, '삭제된 계정입니다.') AS nickname
FROM board b LEFT JOIN users u ON b.user_id = u.user_id
WHERE board_no = #{boardNo}

mapper.update:
UPDATE board
   SET title = #{title}
      ,content = #{content}
      ,upd_date = now()
WHERE board_no = #{boardNo}
  AND user_id = #{userId}

UPDATE board
SET join_cnt = join_cnt + 1
WHERE board_no = #{boardNo}
